package net.frank.android.util;import java.text.DateFormat;import java.text.SimpleDateFormat;import java.util.Date;public class DateUtil {		private static final String DEFAULT_DATE_FORMAT = "yyyy/MM/dd";	public static final String MONTH_DATE_FORMAT = "yyyy/MM";		public static String formatDate(Date date, String formatString) {		DateFormat df = new SimpleDateFormat(formatString);		return df.format(date);	}	public static String formatDate(Date date) {		return formatDate(date, DEFAULT_DATE_FORMAT);	}}